// Actions
export const setCampaignsAction = (payload : any) => ({
  type: SET_CAMPAIGNS,
  payload,
});

export const setCampaignAction = (payload : any) => ({
  type: SET_CAMPAIGN,
  payload,
});

export const getSingleCampaignAction = (payload : any) => ({
  type: GET_SINGLE_CAMPAIGN,
  payload,
});

// reducer
export const campaignsReducer = (state: any = initialState, action: any = undefined) => {
  switch (action.type) {
    case SET_CAMPAIGNS:
      return {
        ...state,
        campaigns: action.payload,
      };
    case SET_CAMPAIGN:
      return {
        ...state,
        campaign: action.payload,
      };
    case GET_SINGLE_CAMPAIGN:
      return {
        campaigns: [...state.campaigns.filter((campaign) => campaign.id === action.payload.id)]
      };

    default:
      return state;
  }
};